import * as React from 'react';
import { View, Text, Button, StyleSheet, TouchableOpacity } from 'react-native';
import { NavigationContainer, DrawerActions } from '@react-navigation/native';
import {
  createDrawerNavigator,
  DrawerContentScrollView,
  DrawerItemList,
  DrawerItem,
} from '@react-navigation/drawer';
import { useWindowDimensions } from 'react-native';


function Feed({ navigation }) {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Feed Screen</Text>
      <Button
        title="Open drawer"
        onPress={() => navigation.dispatch(DrawerActions.openDrawer())}
      />
      <Button
        title="Toggle drawer"
        onPress={() => navigation.dispatch(DrawerActions.toggleDrawer())}
      />
    </View>
  );
}

function Notifications() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Notifications Screen</Text>
    </View>
  );
}

function CustomDrawerContent(props) {
  return (
    <DrawerContentScrollView {...props}>
      <View style={styles.mainContent}>
        <Text>Viktor</Text>
        <DrawerItemList {...props} />
        <DrawerItem
          label="Close drawer"
          onPress={() => props.navigation.dispatch(DrawerActions.closeDrawer())}
        />
        <DrawerItem
          label="Toggle drawer"
          onPress={() => props.navigation.dispatch(DrawerActions.toggleDrawer())}
        />
        <View style={styles.borderItem}></View>
        <TouchableOpacity style={styles.signOutButton}>Sign Out</TouchableOpacity>
      </View>
    </DrawerContentScrollView>
  );
}

const Drawer = createDrawerNavigator();

function MyDrawer() {
  const dimensions = useWindowDimensions();
  return (
    <>
      <Drawer.Navigator drawerType={dimensions.width >= 1200 ? 'permanent' : 'front'}
        drawerContentOptions={{
          activeTintColor: '#e91e63',
          inactiveTintColor: '#fff',
          itemStyle: { marginVertical: 5, color: '#fff' },
        }}
        style={styles.drawerItems} drawerContent={props => <CustomDrawerContent {...props} />}>
        <Drawer.Screen name="Start" component={Feed} />
        <Drawer.Screen name="Your Cart" component={Notifications} />
        <Drawer.Screen name="Favourites" component={Notifications} />
        <Drawer.Screen name="Your Orders" component={Notifications} />
      </Drawer.Navigator>
    </>
  );
}

export default function App() {
  return (
    <NavigationContainer>
      <MyDrawer />
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  mainContent: {
    backgroundColor: '#1F1B33',
    borderTopLeftRadius: 48,
    display: 'flex',
    height: '100%',
    justifyContent: 'flex-end'
    // alignItems: 'flex-end',
    // alignContent: 'flex-end'
  },
  drawerItems: {
    color: '#fff',
  },
  signOutButton: {
    color: '#fff',
    backgroundColor: 'none'
  },
  borderItem: {
    height: 1,
    width: 175,
    backgroundColor: '#fff',
    marginBottom: 50,

  }

});



